<?php/* @var $exception \Exception|null *//* @var $_SERVER array *//* @var $this \yii\web\View */$error_code = strtoupper(substr(sha1(time()), 0, 8));$url = "http://{$_SERVER['HTTP_HOST']}{$_SERVER['REQUEST_URI']}";$get = json_encode($_GET, JSON_PRETTY_PRINT);$post = json_encode($_POST, JSON_PRETTY_PRINT);$cookie = json_encode($_COOKIE, JSON_PRETTY_PRINT);$content = "DATE: " . date("Y-m-d H:i:s") . "\r\n";$content .= "URL: " . $url . "\r\n";$content .= "GET: " . $get . "\r\n";$content .= "POST: " . $post . "\r\n";$content .= "COOKIE: " . $cookie . "\r\n";$content .= "MESSAGE: " . $exception->getMessage() . "\r\n";$content .= "EXCEPTION: " . $exception . "\r\n";$content .= "EXCEPTION END \r\n";file_put_contents("errors/" . $error_code . ".txt", $content);?><h1>Belső rendszerhiba történt</h1><p><strong>Hibakód: </strong> <?=$error_code?></p><br><a href="/errors/errors/create?code=<?=$error_code?>"><button class="btn btn-primary">Hibabejelentés küldése</button></a>